import tkinter as tkimport scrapingimport logginglogging.basicConfig(level=logging.INFO)# windowroot = tk.Tk(screenName="IOF",baseName="iof",className="IOF",useTk=1)root.title('Employment Finder')# dimensionsroot.geometry("320x700+600+50")# variables for linkscurr_link_row = 4# entry listsentry_lists = []keyword = ""def confirm_keyword(entry):    global keyword    keyword = entry.get()    # event listenersdef add_link(confirm_button):   global curr_link_row   new_label = tk.Label(root,text="New Company")   new_label.grid(row=curr_link_row,column=2)      new_entry = tk.Entry(root)   new_entry.grid(row=curr_link_row,column=3)   entry_lists.append(new_entry)      delete_button = tk.Button(root,text='Remove',command=lambda: destroy_link(new_label,new_entry,delete_button),bg="red")   delete_button.grid(row=curr_link_row,column=4)   curr_link_row += 1      confirm_button.grid_forget()   confirm_button.grid(row=curr_link_row+1,column=3)      def destroy_link(label,entry,button):        label.destroy()    entry_lists.remove(entry)    entry.destroy()    button.destroy()                # GUI elementstitle = tk.Label(root, text='Welcome To Employment Finder!')title.grid(row=0,column=3)add_keyword_label = tk.Label(root,text='Enter Keyword').grid(row=2,column=2)add_keyword_entry = tk.Entry(root)add_keyword_entry.grid(row=2,column=3)add_keyword_submit = tk.Button(root,text='Submit',command=lambda: confirm_keyword(add_keyword_entry),bg="green").grid(row=2,column=4)confirm_links_button = tk.Button(root,text='Confirm Companies',command=lambda: scraping.search_links(entry_lists,keyword),bg="green")add_link_button = tk.Button(root,text='Add Company',width=25,command=lambda: add_link(confirm_links_button))add_link_button.grid(row=3,column=3)confirm_links_button.grid(row=curr_link_row+1,column=3)curr_link_row += 1root.mainloop()